class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each(function(){t.deleteComment($(this))})}createComment(e){let t=this;this.newCommentForm.submit(function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(o){console.log(o);let n=t.newCommentDom(o.data.comment);$(`#post-comments-${e}`).prepend(n),t.deleteComment($(" .delete-comment-button",n)),new ToggleLike($(" .toggle-like-button",n)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}newCommentDom(e){return $(`<li class="comment-${e._id}">\n\n        ${e.content} \n        <small id="delete">\n            <a  class="delete-comment-button" href="/comments/destroy/${e._id}">Delete</a>\n        </small>\n        <br/>\n        \n        <small id="user-comment"> ${e.user.name} </small>\n        <br />\n        <small>\n                            \n            <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                0 Likes\n            </a>\n                            \n        </small>\n    </li>\n    `)}deleteComment(e){$(e).click(function(t){t.preventDefault(),console.log("inside the delte comment"),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){console.log(e),$(`.comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})})}}